// search videos
yt.searchVideos = function (user_search, page, filters, order, cb){
    videos_responses = new Array();
    if (page === 1){
        current_start_index = 1;
    } else {
        current_start_index = (page - 1) * 25 + 1;    
    }
    var params = {};
    if (filters === '') {
        params = {
                    'q':              ''+user_search+'',
                    'start-index' : ''+current_start_index+'',
                    'max-results':  25,
                    'orderby':        ''+order+'',
                }
    } else if (filters === 'hd') {
        params = {
                    'q':              ''+user_search+'',
                    'start-index' : ''+current_start_index+'',
                    'max-results':  25,
                    'orderby':        ''+order+'',
                    'hd' : true
                }
    } else if (filters === '3d') {
        params = {
                    'q':              ''+user_search+'',
                    'start-index' : ''+current_start_index+'',
                    'max-results':  25,
                    'orderby':        ''+order+'',
                    '3d' : true
                }
    }
    try{
        youtube.feeds.videos(params,
            function( err, data ) {
            if( err ) {
                cb(err);
            } else {
                cb(data);
            }
        });
    }catch(err){
        console.log('searchVideos err: '+err);
    }
}

